function ContactForm(props: any) {
  let usermessage: SiteContent.Message;

  const { value: f_name, bind: bindname, reset: resetname } = useInput("");
  const { value: f_email, bind: bindemail, reset: resetemail } = useInput("");
  const {
    value: f_subject,
    bind: bindsubject,
    reset: resetsubject,
  } = useInput("");
  const {
    value: f_message,
    bind: bindmessage,
    reset: resetmessage,
  } = useInput("");
  const {
    value: f_contactno,
    bind: bindcontactno,
    reset: resetcontactno,
  } = useInput("");
  const [isSuccessfullySent, setIsSuccessfullySent] = useState(false);
  const [alertMessage, setalertMessage] = useState("");

  const handleSubmit = (evt: any) => {
    evt.preventDefault();
    usermessage = {
      name: f_name,
      email: f_email,
      subject: f_subject,
      message: f_message,
      contactno: f_contactno,
    };
    if (
      usermessage.name.trim() !== "" &&
      usermessage.email.trim() !== "" &&
      usermessage.subject.trim() !== "" &&
      usermessage.message.trim() !== ""
    ) {
      if (dataapi.createMessage(usermessage)) {
        setalertMessage(
          "We have received your request. We will contact you soon"
        );
        setIsSuccessfullySent(true);
        setTimeout(() => setIsSuccessfullySent(false), 3000);
      } else {
        setIsSuccessfullySent(false);
      }
      resetname();
      resetemail();
      resetsubject();
      resetmessage();
      resetcontactno();
    } else {
      setalertMessage("Please provide all inputs in Contact Form");
      setIsSuccessfullySent(true);
      setTimeout(() => setIsSuccessfullySent(false), 3000);
    }
  };

  return (
    <div className="form">
      <div id="sendmessage">Your message has been sent. Thank you!</div>
      <div id="errormessage"></div>
      <form onSubmit={handleSubmit} role="form" className="contactForm">
        <div className="form-row">
          <div className="form-group col-lg-6">
            <input
              type="text"
              {...bindname}
              className="form-control"
              id="name"
              placeholder="Your Name"
              data-rule="minlen:4"
              data-msg="Please enter at least 4 chars"
            />
            <div className="validation"></div>
          </div>
          <div className="form-group col-lg-6">
            <input
              type="email"
              {...bindemail}
              className="form-control"
              name="email"
              id="email"
              placeholder="Your Email"
              data-rule="email"
              data-msg="Please enter a valid email"
            />
            <div className="validation"></div>
          </div>
          <div className="form-group col-lg-6">
            <input
              type="text"
              {...bindcontactno}
              className="form-control"
              name="contactno"
              id="contactno"
              placeholder="Your Contact"
              data-msg="Please enter your contact number"
            />
            <div className="validation"></div>
          </div>
        </div>
        <div className="form-group">
          <input
            type="text"
            className="form-control"
            {...bindsubject}
            id="subject"
            placeholder="Subject"
            data-rule="minlen:4"
            data-msg="Please enter at least 8 chars of subject"
          />
          <div className="validation"></div>
        </div>

        <div className="form-group">
          <textarea
            className="form-control"
            {...bindmessage}
            rows={5}
            data-rule="required"
            data-msg="Please write something for us"
            placeholder="Message"
          ></textarea>

          <div className="validation"></div>
        </div>
        {isSuccessfullySent && (
          <div className="form-group">
            <div className="col-md-6 info">
              <i className="ion-ios-email-outline"></i>
              <p>{alertMessage}</p>
            </div>
          </div>
        )}
        <div className="text-center">
          <button type="submit" title="Send Message">
            Send Message
          </button>
        </div>
      </form>
    </div>
  );
}

