import { useState } from "react";;

export const useInput = (initialValue: any) =>{
    const[value, setValue] = useState(initialValue);

    return{
        value,
        setValue,
        reset:()=> setValue(""),
        bind:{
            value,
            onchange : (event: { target: { value: any; }; })  =>{
                setValue(event.target.value);
            }
        }
    };
};
