import './css/style.css'
import './lib/bootstrap/css/bootstrap.min.css';
import './lib/font-awesome/css/font-awesome.min.css'
import './lib/animate/animate.min.css'
import './lib/ionicons/css/ionicons.min.css'
import './lib/owlcarousel/assets/owl.carousel.min.css'
import './lib/lightbox/css/lightbox.min.css'
import logo from './img/logo.png';
import intro from './img/intro-img.svg';
import {
    Routes,
    Route,
    Outlet,
    Link,
    useSearchParams,
    useParams,
    LinkProps
  } from "react-router-dom";


import {api} from './components/impl/api';
import { SiteContent } from './components/site/site';

let dataapi = new api();

function App() {
 return(
  // <Routes>
  //   <Route path="/" element={<Layout/>}>
      
  //   </Route>
  // </Routes>
  <Layout/>
 );
}


function Layout()
{
  let Menus : SiteContent.Menu[] = dataapi.createMenu();
  
  return (
    
    <div >
      <header id="header" className="fixed-top">
    <div className="container">

      <div className="logo float-left">
        <a href="#intro" className="scrollto"><img src={logo} alt="" className="img-fluid"></img></a>
      </div>
      <nav className="main-nav float-right d-none d-lg-block">
        <ul>
          
          {Menus.map(menu =>(
            <li key={menu.name}> 
            {/* <a href='#'> {menu.name}</a> */}
            <MenuLink menu={menu.name}>{menu.name}</MenuLink>
            </li>
          ))}
        </ul>
      </nav>
      
    </div>
  </header>
  <Intro/>
  <Outlet/>
    </div>
  );
}

interface MenuLinkProps extends Omit<LinkProps,"to">{
  menu: string;
}

function MenuLink({menu, children, ...props} : MenuLinkProps){
  let [searchParams] = useSearchParams();
  let isActive = searchParams.get("menu") === menu;
  return (
    <Link
       to ={`/?menu= ${menu}`}
       {...props}
       >
         {children}
     </Link>
  );
}

function Intro()
{
  let introdata : string = dataapi.createIntro();
    
  return(
    <div>
    <section id="intro" className="clearfix">
    <div className="container">

      <div className="intro-img">
        <img src={intro} alt="" className="img-fluid"></img>
      </div>
      
      <div className="intro-info">
      <div dangerouslySetInnerHTML={{ __html: introdata }}></div>
        <div>
          <a href="#about" className="btn-get-started scrollto">Get Started</a>
          <a href="#services" className="btn-services scrollto">Our Services</a>
        </div>
      </div>

    </div>
  </section>
  </div>
  );
}



export default App;
